import React from "react";

import { StyleSheet, View ,Text,Image,StatusBar} from "react-native";
import AppIntroSlider from "react-native-app-intro-slider";
import Icon from 'react-native-vector-icons/Ionicons';
const styles = StyleSheet.create({
    buttonCircle: {
      width: 40,
      height: 40,
      color:'black',
    //   backgroundColor: 'rgba(0, 0, 0, .2)',
      borderRadius: 20,
      justifyContent: 'center',
      alignItems: 'center',
    },
    slide:(bg)=>({
        paddingTop: StatusBar.currentHeight,
        paddingLeft:30,
        paddingRight:30,
        backgroundColor:'white',
        height:'100%',
        justifyContent:'center'
    }),
    title: (size,weight,color)=>({
        fontSize:size,
    fontFamily:'Roboto',
    color:color,
    fontWeight:weight,
    lineHeight:45,
    // letterSpacing:.5
    }),
    text:{
        paddingTop:10,
        fontWeight:'300',
        fontSize:15
    }
    //[...]
  });
// const navigation = useNavigation()
const slides = [
  {
    key: 1,
    titleSize:30,
    titleWeight:'700',
    title: "Welcome to \nMindcraft",
    text: 'Mental illness is not a choice, but recovery is.',
    image:
      "data:image/jpeg;base64,",
    backgroundColor: "#000000",
  },
  {
    key: 2,
    titleSize:20,
    titleWeight:'600',
    title: "Enhance your mental health by following our advice...",
    
    image:
      "data:image/jpeg;base64,",
    backgroundColor: "#000000",
  },
  {
    key: 3,
    titleSize:20,
    titleWeight:'600',
    title: "We have professionals who can help you.",
    // text: "I'm already out of descriptions\n\nLorem ipsum bla bla bla",
    image: 'data:image/jpeg;base64,',
    backgroundColor: "#000000",
  },
];

export default class App extends React.Component {

      state = {
    showRealApp: false,
  };
  _renderItem = ({ item }) => {
    return (
      <View style={styles.slide(item.backgroundColor)}>
        <Text style={styles.title(item.titleSize,item.titleWeight,item.backgroundColor)}>{item.title}</Text>
        <Image source={{url:item.image}} />
        <Text style={styles.text}>{item.text}</Text>
      </View>
    );
  };
  _onDone = () => {
    // User finished the introduction. Show real app through
    // navigation or simply by controlling state
    this.setState({ showRealApp: true });
  };
  _renderNextButton = () => {
    return (
      <View style={styles.buttonCircle}>
        <Icon
          name="arrow-forward-outline"
          color="#000000e5"
          size={24}
        />
      </View>
    );
  };
  _renderDoneButton = () => {
    return (
      <View style={styles.buttonCircle}>
        <Icon
          name="arrow-forward-outline"
          color="#000000e5"
          size={24}
        />
      </View>
    );
  };
  render() {
    if (this.state.showRealApp) {
        this.props.navigation.push('Topic')
    } else {
      return (
        <AppIntroSlider
          renderItem={this._renderItem}
          renderDoneButton={this._renderDoneButton}
        renderNextButton={this._renderNextButton}
          data={slides}
          onDone={this._onDone}
        />
      );
    }
  }
}
